/*Gotta Catch Em' All */
/*Create Tables and Insert Data*/

DROP TABLE HM_Moves;
DROP TABLE Towns;
DROP TABLE Type;
DROP TABLE Route;
DROP TABLE Evolution;
DROP TABLE Ability;
DROP TABLE pokeHaveHM;
DROP TABLE Pokemon;
DROP TABLE Region;
DROP TABLE pokeHaveType;

CREATE TABLE Region (
    regionName VARCHAR2(20) PRIMARY KEY NOT NULL ,
    regionDes VARCHAR2(300) NOT NULL
);


CREATE TABLE Pokemon (
    pokeID  VARCHAR2(3) PRIMARY KEY NOT NULL,
    name VARCHAR2(20) NOT NULL,
    hp VARCHAR2(3) NOT NULL,
    attack VARCHAR2(3) NOT NULL,
    defense VARCHAR2(3) NOT NULL,
    sp_atk VARCHAR2(3) NOT NULL,
    sp_df VARCHAR2(3) NOT NULL,
    speed VARCHAR2(3) NOT NULL,
    legendary VARCHAR2(1) NOT NULL,
    regionName VARCHAR2(20) NOT NULL,
    description VARCHAR2(300) NOT NULL,

    CONSTRAINT Pokemon_regionName_fk FOREIGN KEY (regionName) REFERENCES Region (regionName)
);


CREATE TABLE HM_Moves (
    HMID VARCHAR2(4) PRIMARY KEY ,
    moveName VARCHAR2(10) NOT NULL,
    moveType VARCHAR2(10) NOT NULL,
    CONSTRAINT HMmoves_type_fk FOREIGN KEY (moveType) REFERENCES type (typeName)
);

CREATE TABLE Towns (
    townName VARCHAR2(20) PRIMARY KEY NOT NULL,
    regionName VARCHAR2(20) NOT NULL,
    gymBadge VARCHAR2(10),
    CONSTRAINT Towns_regionName_fk FOREIGN KEY (regionName) REFERENCES Region (regionName)
);

CREATE TABLE Type (
    typeName VARCHAR2(10) PRIMARY KEY NOT NULL
);

CREATE TABLE pokeHaveType (
    pokeID VARCHAR2(20),
    typeName VARCHAR2(10),
    CONSTRAINT pokeHaveType_pokeID_fk FOREIGN KEY (pokeID) REFERENCES Pokemon (pokeID),
    CONSTRAINT pokeHaveType_typeName_fk FOREIGN KEY (typeName) REFERENCES Type (typeName),
    CONSTRAINT pokeHaveType_typeName_pokeID_pk PRIMARY KEY (typeName, pokeID)
);

CREATE TABLE Route
(
    regionName VARCHAR2(20) NOT NULL,
    routeName  VARCHAR2(20) NOT NULL,
    CONSTRAINT Route_regionName_fk FOREIGN KEY (regionName) REFERENCES Region (regionName),
    CONSTRAINT Route_regionName_routeName_pk PRIMARY KEY (regionName, routeName)
);

CREATE TABLE Evolution (
    evoName VARCHAR2(20) PRIMARY KEY NOT NULL,
    pokeID VARCHAR2(3) NOT NULL,
    evoLevel VARCHAR2(20),
    CONSTRAINT Evolution_pokeID_fk FOREIGN KEY (pokeID) REFERENCES Pokemon (pokeID)
);

CREATE TABLE Ability (
    pokeID VARCHAR2(3) NOT NULL ,
    abilityName VARCHAR2(20) NOT NULL ,
    hiddenAbilityName VARCHAR2(20),
    CONSTRAINT Ability_pokeID_fk FOREIGN KEY (pokeID) REFERENCES Pokemon (pokeID),
    CONSTRAINT Ability_pokeId_abilityName_pk PRIMARY KEY (pokeID, abilityName)
);

CREATE TABLE pokeHaveHM(
    HMID VARCHAR2(4) NOT NULL,
    pokeID VARCHAR2(3) NOT NULL,
    moveName VARCHAR2(20) NOT NULL,
    CONSTRAINT pokeHaveHM_pokeID_fk FOREIGN KEY (pokeID) REFERENCES Pokemon (pokeID),
    CONSTRAINT pokeHaveHM_HMID_pokeID PRIMARY KEY (HMID, pokeID)
);























